            name="form"
            onSubmit={(event) => handleSubmitLogin(event, username, password)}
            className="Login100-form validate-form"
          >
            <span className="Login100-form-title">ĐĂNG NHẬP</span>
            {messageLogin && (
              <div className="error-message">{messageLogin}</div>
            )}
            <div
              className="wrap-input100 validate-input"
              data-validate="Username is required"
            >
              <input
                className="input100"
                type="text"
                name="username"
                value={username}
                placeholder="Username"
                onFocus={handleFocus}
                onBlur={handleBlur}
                onChange={(event) => setUsername(event.target.value)}
              />
              <span className="focus-input100"></span>
              <span className="symbol-input100">
                <i className="fa fa-user" aria-hidden="true"></i>
              </span>
            </div>

            <div
              className="wrap-input100 validate-input"
              data-validate="Password is required"
            >
              <input
                className="input100"
                type="password"
                name="password"
                value={password}
                placeholder="Password"
                onFocus={handleFocus}
                onBlur={handleBlur}
                onChange={(event) => setPassword(event.target.value)}
              />
              <span className="focus-input100"></span>
              <span className="symbol-input100">
                <i className="fa fa-lock" aria-hidden="true"></i>
              </span>
            </div>

            <div className="container-Login100-form-btn">

              <button className="Login100-form-btn">Đăng nhập</button>

